mapper.select:
SELECT A.NOTICE_NO no, A.NOTICE_TITLE title, A.NOTICE_DATE noticeDate, A.department_no departmentNo, A.notice_title title, A.notice_content content, A.notice_view viewCount FROM NOTICES A, DEPARTMENTS B WHERE A.DEPARTMENT_NO = B.DEPARTMENT_NO and A.notice_no = (select min(notice_no) from notices where notice_no > #{no}
and department_no = (select department_no from notices where notice_no = #{no})
) order by A.notice_no desc

SELECT A.NOTICE_NO no, A.NOTICE_TITLE title, A.NOTICE_DATE noticeDate, A.department_no departmentNo, A.notice_title title, A.notice_content content, A.notice_view viewCount FROM NOTICES A, DEPARTMENTS B WHERE A.DEPARTMENT_NO = B.DEPARTMENT_NO and A.notice_no = (select max(notice_no) from notices where notice_no < #{no}
and department_no = (select department_no from notices where notice_no = #{no})
) order by A.notice_no desc

select X.department_no departmentNo, X.notice_no no, X.notice_title title, X.notice_view viewCount, X.department_name departmentName, X.notice_date noticeDate FROM (select ROW_NUMBER() over (order by A.notice_date desc) rn, A.department_no, A.notice_no, A.notice_title, A.notice_view, B.department_name, A.notice_date from NOTICES A, DEPARTMENTS B where A.department_no = B.department_no
and A.department_no = #{value}
order by A.notice_date desc) X WHERE X.rn <= 4
