mapper.select:
select X.payrollNo payrollNo, X.payrollDate payrollDate, X.payrollMonth payrollMonth, X.payrollType payrollType, X.payrollAmount payrollAmount, X.payrollCount payrollCount from (select row_number() over(order by payroll_date desc) as rn,
from payroll_table
payroll_date >= to_date(#{startDate}, 'yyyy-mm-dd')
and payroll_date <= to_date(#{endDate},'yyyy-mm-dd')
) X where X.rn >= #{beginIndex} and X.rn <= #{endIndex}
